      int j_start = p[j][0];
      int j_end = p[j][1];

      // Case 1
      if(j_start <= ans_back && j_end > ans_back){
        ans.pop_back();
        ans.push_back(make_pair(j_start, ans_back));
      }
      // Case 2
      else if (j_start <= ans_back && j_end <= ans_back){
        ans.pop_back();
        ans.push_back(make_pair(j_start, j_end));
      }
      // Case 3
      else {
        ans.push_back(make_pair(j_start, j_end));
      }

      j++;
    }

	// Returning ans would actually give you the intervals where the arrows must be shot.
    return ans.size();
  }
};
```

