class Solution {
public:
    int findMinArrowShots(vector<vector<int>>& p) {
        sort(p.begin(),p.end(),[](vector<int>&a,vector<int>&b){
            if(a[1]==b[1])return a[0]<b[0];
            return a[1]<b[1];
        });
        int ans=0,n=p.size();
        if(n==0)return 0;
