```
public class Solution {
    public int FindMinArrowShots(int[][] points) {
        if (points.Length <= 1) return points.Length;

        //Sort the ranges by start integer
        Array.Sort(points, new ArrayComparer());

        //xStart and xEnd represent the minimum overlapping range of subsequent elements
        //When a range doesn't overlap this range, we will increment shots and create a new overlap range
        int xStart = points[0][0];
        int xEnd = points[0][1];
        int shots = 0;

        for (int i = 1; i < points.Length; i++){
            //The next range doesn't overlap with what we have found so far so we shoot and arrow and find more overlaps
            if (points[i][0] > xEnd){
                shots++;
                xStart = points[i][0];
                xEnd = points[i][1];
                continue;
            }

            //Keep minimizing the current overlapping range
            if (points[i][0] > xStart) xStart = points[i][0];
            if (points[i][1] < xEnd) xEnd = points[i][1];
        }

        //There will always be some last array we need to fire
        shots++;

        return shots;
    }

    public class ArrayComparer : IComparer<int[]> {
        public int Compare(int[] r1, int[] r2){
            return r1[0].CompareTo(r2[0]);
        }
    }
}
```

