class Solution:
    def findMinArrowShots(self, points: List[List[int]]) -> int:
        points.sort(key=lambda x:(x[0], x[1]))

        ans = 1
        l, r = points[0]
        n = len(points)

        for i in range(n):
            if l <= points[i][0] <= r or l <= points[i][1] <= r:
                l = max(l, points[i][0])
                r = min(r, points[i][1])
            else:
                ans += 1
                l, r = points[i][0], points[i][1]
        return ans

