        for(int i=1;i<n;i++)
        {
            if(points[i][0]>curr[1]) 
            {
                curr=points[i] ;
                ans++;
            }
            else 
            {
                curr[0]=max(curr[0],points[i][0]);
                curr[1]=min(curr[1],points[i][1]); 
            }
        return ans;
    }
};
