c++
class Solution {
public:
    int findMinArrowShots(vector<vector<int>>& points) {
        int a=1;  //a=arrow
        int l = points[0][1]; // l= last point
        sort(points.begin(),points.end());
        for(int i=0;i<points.size();i++)
        {
            if(points[i][0]>l)
            {
                a++;
                l = points[i][1];
            }
            else l = min(l, points[i][1]);
        }
         return a;
    }
};

