        intervals.sort()  # Sort in increasing order by the starting time

        lastStart = lastEnd = intervals[0][0]
        ans = []
        for s, e in intervals:
            if s <= lastEnd:
                lastEnd = max(lastEnd, e)
            else:
                ans.append([lastStart, lastEnd])
                lastStart = s
                lastEnd = e
        ans.append([lastStart, lastEnd])
        return ans
