class Solution:
    def merge(self, intervals: List[List[int]]) -> List[List[int]]:
        if len(intervals) == 0: 
            return []
        intervals.sort() #sort the list according to start index
        stack = [intervals[0]] #push the first element into the stack
        for current in intervals[1:]:
            if current[0] <= stack[-1][1]: #if the start of current element is less than or equal to the end of top element of stack then set
                #stack top element end = max( current end, stack top end)
                stack[-1][1] = max(current[1], stack[-1][1])
            else: 
                #else append the current element into stack
                stack.append(current)
        return stack
