class Solution {
public:
    vector<vector<int>> merge(vector<vector<int>>& intervals) {
        vector<vector<int>> ans;

        // sorting intervals by their starting time
        sort(intervals.begin(), intervals.end());

        // keeping track of starting and ending of current interval 
        int startingInterval = intervals[0][0], endingInterval = intervals[0][1];

        // starting from index 1 because we will start comparing current interval from the
        // last interval we have processed so far

        for(int i = 1; i < intervals.size(); ++i) {

            // if starting of current interval is less then or equal to ending of last 
            // processed interval
            // then will update endingInterval so that it could be considered as merged
            if(intervals[i][0] <= endingInterval)
                endingInterval = max(endingInterval, intervals[i][1]);

            // inserting interval as till now we have minimum starting interval and maximum ending
            // interval which is merged so far, and updating our startingInterval and endingInterval
            // with the bounds of current interval
            else {
                ans.push_back({startingInterval , endingInterval});
                startingInterval = intervals[i][0];
                endingInterval  = intervals[i][1];
            }
        }

        // adding last pair which is left because loop will terminate at intervals size
        ans.push_back({startingInterval, endingInterval});
        return ans;
    }
};
