class Solution {
public:
    vector<vector<int>> merge(vector<vector<int>>& intervals) {
        int n = intervals.size();
        sort(intervals.begin(), intervals.end());
        vector<vector<int>>ans;
        for(int i = 0 ; i<n ;i++){
            if(ans.empty()){//1st time ans will be empty then simply push_back intervals in ans
                ans.push_back(intervals[i]);
            }
            else{
                vector<int>&v = ans.back();
                int y = v[1]; // end time of previous intervals compare y with i's start time
                //intervals[i][0] ---> means start time || intervals[i][1] --> end time 
             //in case of overlapping of intervals   
             if(intervals[i][0] <= y){//start time next interv. is <=prev. interval(overlap)
