    public int[][] merge(int[][] intervals) {
        if(intervals == null || intervals.length <= 1) return intervals;

        int len = intervals.length; 
        int[] starts = new int[len]; 
        int[] ends = new int[len]; 

        for(int i = 0; i < len; i++){
            starts[i] = intervals[i][0]; 
            ends[i] = intervals[i][1]; 
        }

        Arrays.sort(starts); // O(nlogn)
        Arrays.sort(ends); 

        int i = 0;   // i -> index for starts[]
        int j = 0;   // j -> index for ends[]
        List<int[]> result = new ArrayList<>(); 

        while(j < len){   // j will be the index that travals faster. No need to worry about i here
            if(j == len - 1 || starts[j + 1] > ends[j]){   // if j reaches the end, add the last interval to result
                result.add(new int[]{starts[i], ends[j]}); 
                i = j + 1; 
            }
            j++; 
        }

        return result.toArray(new int[result.size()][2]); 
    }
