/**
cases: edge overlapping, full overlapping, none
[[1,3],[2,4],[4,5],[6,7],[8,12],[10,11]]
time: O(nlogn) - length of interval array
space: O(n)
*/
class Solution {
    public int[][] merge(int[][] intervals) {
        if (intervals.length <= 1) {
            return intervals;
        }
        Arrays.sort(intervals, (i1, i2) -> Integer.compare(i1[0], i2[0])); // sort by start point

        List<int[]> result = new ArrayList<>();
        int[] currInterval = intervals[0]; // grab first interval to compare

        int i = 1;
        while (i < intervals.length) {
            if (intervals[i][0] > currInterval[1]) { // no overlap
                result.add(currInterval);
                currInterval = intervals[i]; // move to the next interval if no overlap
                if (i == intervals.length-1) { // edge case if we hit last
                    result.add(currInterval);
                }
            }
            else if (currInterval[1] >= intervals[i][0]) { // if overlap
                currInterval[0] = Math.min(currInterval[0], intervals[i][0]);
                currInterval[1] = Math.max(currInterval[1], intervals[i][1]);
                // result.add(currInterval); // no need to add, we are just expanding interval
                if (i == intervals.length-1) { // edge case if we hit last
                    result.add(currInterval);
                }
            }
            i++;
        }
        return result.toArray(new int[result.size()][]); // convert parent List to array
    }
}
