```
// There can be three cases with two intervals 
// Case 1:- No overlap
//  [1,           2]      [3,             4]
//  _______________        ________________
// | left interval |      | right interval |
// |_______________|      |________________|

//Case 2:- Partial overlap
// [1,             3]      
//  _______________
// | left interval |
// |_______________|
//           [2,            4]
//            ________________
//           | right interval |
//           |________________|

// Case 3:- Full overlap
//  [1,                 4]
//  _____________________
// |    left interval    |
// |_____________________|
//   [2,             3]
//    ________________
//   | right interval |
//   |________________|


class Solution 
{
public:
    vector<vector<int>> merge(vector<vector<int>>& v) 
    {
        // sort intervals on basis of start value
        sort(v.begin(),v.end());
        int n=v.size();
		int left=0,right=1;
        vector<vector<int>>ans; // For storing the result

        while(left<n && right<n)
        {
            if(v[left][1]<v[right][0])    // Case 1 : (No overlap )
            {
                left=right;
                right++;
            }
            //  overlap occurs (Case 2 or Case 3)
            else
            {
                if(v[left][1]>=v[right][1])  // Case 3: (Full overlap)
                {
                    // Mark the fully overlapped interval so that it can be deleted later
                    v[right]={-1,-1};
                    right++;
                }
                // Case 2: (partial overlap)
                else
                {
                    // Extend the end point of the left interval( by making end point of left interval=end point of right interval) so that the intervals can be merged 
                    v[left][1]=v[right][1];
                    // Mark the partially overlapped interval so that it can be deleted later
                    v[right]={-1,-1};
                    right++;
                }
            }
        }
        for(int i=0;i<n;i++)
        {
            // If any interval is marked then don't push it to the ans vector cause after merging marked intervals can't have their existence
            if(v[i][0]!=-1)
            {
                ans.push_back(v[i]);
            }
        }
        return ans;
    }
};
```

