class Solution {
public:
    vector<vector<int>> merge(vector<vector<int>>& intervals) 
    {
        //Checking if there is only one interval
        if(intervals.size()<=1)return intervals;

        //Sorting the intervals on the basis of starting time
        sort(intervals.begin(),intervals.end());

        //Creating vector of vectors to store the intervals
        vector<vector<int>>ans;

        //Storing the 1st interval in temp vector(It will containg start time and end time)
        vector<int>temp=intervals[0];

        //It will be vector of intervals start and end time
        for(auto it:intervals)
        {
            //if EndTime of Ith Interval is greate than start of i+1th Interval
            //We'll merge the interval The End Time of merged interval Will be  
            //Max of end time of both the ith and i+1th interval
            if(temp[1]>=it[0])
            {
                temp[1]=max(temp[1],it[1]);

            }
            //If Merging is not possible we'll put the i+1th interval in ans
            //And we'll update the temp with i+1th interval and repeat the process for next
            else
            {
                ans.push_back(temp);
                temp=it;
            }
        }
        //Putting the Last Interval in the ans array
        ans.push_back(temp);

        return ans;

    }
};
