// use a balanced binary search tree to store intervals in sorted order as they come in
// if we add a new one, we need to iterate over all intervals and merge as needed
// as we iterate, if newInterval and current interval overlap, then remove currentInterval and merge
// keep removing and merging until we have no overlap, add merged interval back in
class Solution {
    public int[][] merge(int[][] intervals) {
        TreeSet<int[]> bbst = new TreeSet<int[]>((a, b) -> a[0]-b[0]);
        for(int[] interval: intervals) {
            Iterator<int[]> itr = bbst.iterator();
            while(itr.hasNext()) {
                int[] currInterval = itr.next();
                if(overlap(currInterval, interval)) {
                    interval = merge(currInterval, interval);
                    itr.remove();
                }
            }
            bbst.add(interval);
        }

        int size = bbst.size();
        int[][] res = new int[size][2];
        int idx = 0;
        for(int[] interval: bbst) {
            res[idx] = interval;
            idx++;
        }
        return res;
    }

    public boolean overlap(int[] intervalA, int[] intervalB) {
        return Math.max(intervalA[0], intervalB[0]) <= Math.min(intervalA[1], intervalB[1]);
    }

    public int[] merge(int[] intervalA, int[] intervalB) {
        int start = Math.min(intervalA[0], intervalB[0]);
        int end = Math.max(intervalA[1], intervalB[1]);
        return new int[]{start, end};
    }
}
