class Solution {
public:
    //compartor for sorting the intervals
    static bool compare(vector<int> &a,vector<int> &b){
        if(a[0]==b[0]){
            return a[1]<b[1];
        }
        return a[0]<b[0];
    }
    vector<vector<int>> merge(vector<vector<int>>& nums) {
        sort(nums.begin(),nums.end(),compare);
        // sorting the intervals
        vector<vector<int>> res;
        res.push_back(nums[0]);
        // pushing 1st interval to result
        for(int i=1;i<nums.size();i++){
            // checking if current interval overlaps with last interval
            if(res[res.size()-1][1] >= nums[i][0]){
				// we just update out interval
				// y here should be there larger of 2 intervals
                res[res.size()-1][1]=max(res[res.size()-1][1],nums[i][1]);
            }else{
                res.push_back(nums[i]);
            }
        }

        return res;
    }
};
