     static int[][] sort(int[][] arr){
        if(arr.length==1){
            return arr;
        } else if (arr.length==2) {
            int[][] ans = new int[2][];
            if(arr[0][0]<arr[1][0]){
                ans[0]=arr[0];
                ans[1]=arr[1];
            }else{
                ans[0]=arr[1];
                ans[1]=arr[0];
            }
            return ans;
        }
        int mid = arr.length/2;
        int[][] arr1,arr2;
        if(arr.length%2!=0){
             arr2 = new int[(arr.length+1)/2][];
             arr1 = new int[mid][];
        }else{
             arr1 = new int[mid][];
             arr2 = new int[mid][];
        }
        for(int i=0;i<arr.length;i++){
            if(i<mid){
                arr1[i]=arr[i];
            }else{
                arr2[i-mid]=arr[i];
            }
        }
        arr1=sort(arr1);
        arr2=sort(arr2);

        int[][] ans=new int[arr.length][];
        int i1=0,i2=0;
        int i=0;
        for(;i<ans.length && i1<arr1.length && i2<arr2.length;i++){
            if(arr1[i1][0]<arr2[i2][0]){
                ans[i]=arr1[i1];
                i1++;
            }else{
                ans[i]=arr2[i2];
                i2++;
            }
        }
        if(i1==arr1.length && i2!=arr2.length){
            while(i<ans.length){
                ans[i]=arr2[i2];
                i2++;
                i++;
            }
        } else if (i2==arr2.length && i1!=arr1.length) {
            while(i<ans.length){
                ans[i]=arr1[i1];
                i1++;
                i++;
            }
        }
        return ans;
    }

    public int[][] merge(int[][] intervals) {
        intervals=sort(intervals);
        int n = intervals.length;
        if(n==1){
            return intervals;
        }
         for(int i=0;i<intervals.length-1;i++){
            int max1 = intervals[i][1];
            if(max1>=intervals[i+1][0]){
               while(i<intervals.length-1 && max1>=intervals[i+1][0]){
                   max1=Math.max(max1,intervals[i+1][1]);
                   n--;
                   i++;
               }
            }
        }
        int[][] ans = new int[n][2];
        int i=0,j=0;
        for(;i<intervals.length-1;i++,j++){
           int min = intervals[i][0];
           int max = intervals[i][1];
           while(i<intervals.length-1 && max>=intervals[i+1][0]){
               min=Math.min(min,intervals[i+1][0]);
               max=Math.max(max,intervals[i+1][1]);
               i++;
           }
           ans[j][0]=min;
           ans[j][1]=max;
        }
        if(j==ans.length-1 && i==intervals.length-1){
            ans[j]=intervals[i];
        }
        return ans;
    }
}

class Solution {
public:
    vector<vector<int>> merge(vector<vector<int>>& intervals) {
        vector<vector<int>> result;
        int i;
        if(intervals.size()==1){
            return intervals;
        }
        sort(intervals.begin(), intervals.end());

        if(intervals[1][0]<=intervals[0][1]){
                if(intervals[0][1]>intervals[1][1]){
                    result.emplace_back(vector<int>{intervals[0][0],intervals[0][1]});
                }
                else{
                    result.emplace_back(vector<int>{intervals[0][0],intervals[1][1]});
                }
        }
        else{
             result.emplace_back(vector<int>{intervals[0][0],intervals[0][1]});
             result.emplace_back(vector<int>{intervals[1][0],intervals[1][1]});
        }
        if(intervals.size()==2){
            return result;
        }
        for(i=2;i<intervals.size();i++){
            if(intervals[i][0]<=result.front()[1]){
                if(intervals[i][1]>result.front()[1]){
                    result.front()[1]=intervals[i][1];
                }
            }
            else{
                result.emplace_back(vector<int>{intervals[i][0],intervals[i][1]});
            }

        }

    return result;

    }
};

