class Solution {
public:
    bool find132pattern(vector<int>& nums) {
        int n = nums.size();
        if(n < 3)
            return false;
        set<int> st;
        vector<int> left_min(n+1);
        left_min[0] = nums[0];

        for(int i = 1 ; i < n ; ++i )
            left_min[i] = min(left_min[i-1] , nums[i]);

        st.insert(nums[n-1]);
        for(int i = n - 2 ; i >= 1 ; --i)
        {
            int mn = left_min[i-1];
            if(nums[i] > mn)
            {
                auto it = st.upper_bound(mn);
                if(it != st.end())
                {
                    if(*it < nums[i])
                        return true;
                }
            }
            st.insert(nums[i]);
        }
        return false;
    }
};
```

