class Solution {
    public boolean find132pattern(int[] nums) {
        int min[] = new int[nums.length];
        min[0] = nums[0];
        for(int i =1;i<nums.length;i++) min[i] = Math.min(min[i-1],nums[i]); // maintain minimum so far 
        Stack<Integer> st = new Stack();
        for(int j = nums.length-1;j>=0;j--){
            while(!st.isEmpty()&&st.peek()<=min[j]) st.pop(); // st.peek() --> nums[k] , min[j] -->  possible nums[i] which should be smaller than st.peek();
            if(!st.isEmpty()&&st.peek()<nums[j]){ // if stack is not empty and we have exited the while loop above then that means nums[i] < nums [k] so check if nums[k] < nums[j] if yes return true 
                return true;
            }
            st.push(nums[j]);
        }
        return false;
    }
}
