class Solution {
public:
    bool find132pattern(vector<int>& nums) {
        if(nums.size()<3)
            return false;
        set<int> s;
        int n=nums.size();
        map<int,int> m;
        for(int i=n-1;i>=0;i--)
        {
            s.insert(nums[i]);
            m[nums[i]]++;
        }
        int minv=nums[0];
        m[nums[0]]--;
        if(m[nums[0]]==0)
        s.erase(minv);
        for(int i=1;i<n;i++)
        {
            if(s.upper_bound(minv)!=s.end() && nums[i]>*(s.upper_bound(minv)) && *(s.upper_bound(minv))!=minv)
            {
                return true;
            }
             m[nums[i]]--;
             if(m[nums[i]]==0)
            s.erase(nums[i]);

            if(nums[i]<minv)
            {
                minv=nums[i];
            }
        }
        return false;

    }
};
