class Solution {
public:
    bool find132pattern(vector<int>& nums) {
        int n = nums.size();
        if(n<3) return false; // atleast 3 nums reqd

        vector<int>preMin(n,INT_MAX); // track min value in range 0 to i-1
        for(int i=1;i<n;i++)
            preMin[i] = min(preMin[i-1],nums[i-1]);// store prefix min values

        stack<int>stk;

        for(int i=n-1;i>=0;i--){// Loop through end to start to store the next greater number
            while(!stk.empty() && stk.top()<=preMin[i]) // as we are finding next greater number than preMin[i], pop numbers from stack which are less or equal to preMin[i]
                stk.pop();

            if(!stk.empty() && stk.top()< nums[i]) return true;// if stack.top < nums[i] then it is a 132 pattern as stack.top > preMin[i] therefore there exists a num in nums[i] which is smaller than both nums[i] and stk.top()

            stk.push(nums[i]);
        }
        return false;//if nothing is found
    }
};
//Regarding Time Complexity, we are iterating every element twice, once during findind premin array and second during the main loop, therefore overall O(N)
