class Solution {

    public boolean find132pattern(int[] nums) {

        int len = nums.length;

        // For each element, store the minimum element that exists on left side.
        int[] min = new int[len];
        min[0] = nums[0];
        for (int i = 1; i < len; i++) {
            min[i] = Math.min(min[i - 1], nums[i]);
        }

        Stack<Integer> stack = new Stack();
        for (int k = 0; k < len; ) {
            if (stack.size() == 0) {
                stack.add(k++);
            }
            else if (nums[stack.peek()] > nums[k]) { 
                // Top element is greater than cur element => top = j, cur = k

                // If there exists i for the top element then we found a triplet.
                if (min[stack.peek()] < nums[k]) {
                    return true;
                }

                // Add cur to stack(stack will always have elements in dec order).
                stack.add(k++);
            }
            else {
                /* 
                    If top element is smaller, thus indicating that 
                    there is no element on left side that is greater 
                    than cur element.

                    Now, we will remove all elements from stack
                    that are smaller than cur. 

                    As they will not contribute to any future cur element as j.
                    Because we just found a new element that is larger than
                    all of them and can be used for j for future cur's.
                */
                while (stack.size() > 0 && nums[stack.peek()] <= nums[k]) {
                    stack.pop();
                }
            }
        }
        return false;
    }
}
