        for j in range(len(nums) - 1, -1, -1):
            if nums[j] > min_list[j]:

                while stack and stack[-1] <= min_list[j]:
                    stack.pop()

                if stack and stack[-1] < nums[j]:
                    return True

                stack.append(nums[j])

