```
class Solution {
public:
    bool find132pattern(vector<int>& nums) {

        int i, sk = INT_MIN;
        stack<int> st;
        int n = nums.size();

        for(i=n-1;i>=0;i--)
        {
            if(st.empty() || nums[i] < st.top())
                st.push(nums[i]);
            else if(nums[i] > st.top())
            {
                while(!st.empty() && nums[i] > st.top())
                {
                    sk = st.top();
                    st.pop();
                }
                st.push(nums[i]);
            }
            if(nums[i] < sk)
                return true;
        }
        return false;
    }
};
