namespace atcoder {

template <class T> struct fenwick_tree {
    using U = internal::to_unsigned_t<T>;

  public:
    fenwick_tree() : _n(0) {}
    fenwick_tree(int n) : _n(n), data(n) {}

    void add(int p, T x) {
        assert(0 <= p && p < _n);
        p++;
        while (p <= _n) {
            data[p - 1] += U(x);
            p += p & -p;
        }
    }

    T sum(int l, int r) {
        assert(0 <= l && l <= r && r <= _n);
        return sum(r) - sum(l);
    }

  private:
    int _n;
    std::vector<U> data;

    U sum(int r) {
        U s = 0;
        while (r > 0) {
            s += data[r - 1];
            r -= r & -r;
        }
        return s;
    }
};

}  // namespace atcoder

using namespace atcoder;

class Solution {
public:
  bool find132pattern(vector<int>& a) {

    int n = a.size();
    int idx = 0;
    map<int,int> mp;
    for (auto x : a) mp[x] = 0;
    for (auto &[k,v] : mp) v = idx++;
    for (auto &x : a) x = mp[x];

    fenwick_tree<int> fw(idx);
    for (auto x : a) fw.add(x,1);

    int lo = 1e9;
    int hi = -1;

    bool ret = false;

    for (auto x : a) {
      if (x < lo) {
        lo = x;
        hi = x;
      }
      else if (x > hi) hi = x;

      fw.add(x,-1);
      if (lo + 1 < hi) {
        if (fw.sum(lo+1,hi) > 0) ret = true;
      }
    }

    return ret;
  }
};
