bool find132pattern(vector<int>& nums) {
        if(nums.size()<3)
            return false;
        stack<int> S;
        vector<int> Min;
        int min_now=INT_MAX;
        for(int i=0;i<nums.size();++i){
            min_now=min(min_now,nums[i]);              // create the min array/vector
            Min.push_back(min_now);
        }
        for(int i=nums.size()-1;i>=0;--i){
            if(nums[i]>Min[i])                  // possible i j pair
            {
				while(!S.empty()&& S.top()<nums[i])          // if stack is not empty and its top value is smaller than the j val
				{
					{
						if(S.top()>Min[i])                           // if top val is also smaller than the min val then this is the perfect i j k triplet hence return 
							return true;
						S.pop();                                       // if not then pop the stack to see the next element 
					}
				}
             S.push(nums[i]);                      // if top of stack is not smaller than j val then just push the j val onto stack 
            }
        }
        return false; 
    }

