class Solution {
public:
    bool find132pattern(vector<int>& nums) {
        if(nums.size()<3) return(false);
        int i=nums[0], j=INT_MIN, icand=INT_MAX, jcand=INT_MIN;

        for(int p=1;p<nums.size();p++){
            if(nums[p]<i) {
                icand=min(icand,i);
                jcand=max(jcand,j);
                i=nums[p];
                j=INT_MIN;
            }
            else if((nums[p]<j && nums[p]>i) || (nums[p]<jcand && nums[p]>icand)){
                return(true);
            }
            else if(nums[p]>i){                
                j=max(j,nums[p]);
            }            
        }
        return(false);        
    }
};
