var find132pattern = function(nums) {
    /*
    nums = [1,3,2,4,5]
    Idea is to find a number that has min left and 2nd higest min on right of that number
    Iterate numbers from the right
    Use stack to find 2nd highest min(compare previous number to number in stack - if the previous num is higher then pop() the last value of stack) Ex: pop 2 as 3 > 2 
    Compare that 2nd highest min to the num before it, and if that is less than 2nd highest min return true Ex: compare 2 (2nd highest min) to 1. 1< 2 return truw
    If none of above onditions meet return false
    */
    var stack=[], 
    min = -Infinity;
    for(var i=nums.length-1; i>=0; i--){
        //Compare that 2nd highest min to the num before it, and if that is less than 2nd highest min return true
        if(nums[i] < min) 
            return true;
        else{
            //Use stack to find 2nd highest min(compare previous number to number in stack - if the previous num is higher then pop() the last value of stack)
            while(stack.length>0 && nums[i]> stack[stack.length-1]){
            min = stack.pop();
            }
            stack.push(nums[i]);
        } 
    }
    return false;

};
