class Solution {
public:
    int poorPigs(int buckets, int minutesToDie, int minutesToTest) {
        //speed up
        if(buckets < 2) return 0;
        if(minutesToTest < minutesToDie) return -1;
        int noTests = minutesToTest/minutesToDie;
        if(noTests == 1) return buckets-1;
        if(noTests >= buckets-1) return 1;
        //dp
        vector<vector<int>> dp(buckets+1, vector<int>(noTests+1, 0));
        for(int i = 1; i <= buckets; i++)
        {
            for(int j = 1; j <= noTests; j++)
            {
                if( i == 1) 
                {
                    dp[i][j] = 0;
                    continue;
                }
                if( j == 1) 
                {
                    dp[i][j] = i-1;
                    continue;
                }
                if(j >= i - 1)
                {
                    dp[i][j] = 1;
                    continue;
                }
                dp[i][j] = std::max(1 + dp[i/2][j-1], dp[(i+1)/2][j-1]); //(pig drinks left half, dp = max(pig dead, pig live))
            }
        }
        return dp[buckets][noTests];
    }
};
```

