var permute = function(nums) {
    if (nums.length === 1) {
        return [nums.slice()];
    }

    var res = [];

    for (var i = 0; i < nums.length; i++) {
        var n = nums.shift();
        var perms = permute(nums.slice());

        for (var p of perms) {
            p.push(n);
        }

        res.push(...perms);
        nums.push(n);
    }

    return res;  
};
