            new_nums = list(nums)
            new_nums[i], new_nums[j] = new_nums[j], new_nums[i]
            return new_nums

        result = [nums,]

        for i in range(len(nums)-1):
            for one in result[:]:
                for j in range(i+1, len(nums)):
                    result.append(swap(i, j, one))

