class Solution {
public:
    vector<vector<int>> result;
    void permuteRec(vector<int>& nums, int begin) { 
            if (begin == nums.size()) { 
                        result.push_back(nums); 
            return; 
            } 
        for (int i = begin; i < nums.size(); i++) { 
            swap(nums[begin], nums[i]); 
            permuteRec(nums, begin + 1); 
            swap(nums[begin], nums[i]); 
        } 
    } 

    vector<vector<int>> permute(vector<int>& nums) { 
        permuteRec(nums, 0); 
        return result; 

    }
};

