class Solution {
private:
    void solve(vector<int>&nums, int index, vector<vector<int>>&ans){
        if(index>=nums.size()){
            ans.push_back(nums);
            return ;
        }

        for(int i=index; i<nums.size(); i++){
            swap(nums[index], nums[i]);
            solve(nums, index+1, ans);
            swap(nums[index], nums[i]);
        }
    }
public:
    vector<vector<int>> permute(vector<int>& nums) {
        vector<vector<int>>ans;
        int index=0;
        solve(nums, index, ans);
        return ans;
    }
};

simple c++ approach to solve this question:

class Solution {
    private:
    void helper(vector<vector<int>>&ans,vector<int>nums,vector<int>&curr,unordered_map<int,bool>&m1 ){

        if(curr.size()==nums.size()){
            ans.push_back(curr);
            return ;
        }

        for(int i=0;i<nums.size();i++){
            if(!m1[nums[i]]){
                curr.push_back(nums[i]);
                m1[nums[i]]=true ;

                helper(ans,nums,curr,m1);
                m1[nums[i]]=false ;
                curr.pop_back();
            }
        }

    }
public:
    vector<vector<int>> permute(vector<int>& nums) {

        vector<vector<int>>v1 ;
        vector<int>curr ;
        unordered_map<int,bool>m1 ;
        helper(v1,nums,curr,m1);
        return v1 ;


    }
};

