def getPermutation(self, n, k):
    nums = range(1, n+1)
    for i in xrange(k-1):
        self.nextPermutation(nums)
    return "".join(map(str, nums))

def nextPermutation(self, nums):
    l = d = m = len(nums)-1
    while l > 0 and nums[l] <= nums[l-1]:
        l -= 1
    if l == 0:
        nums.reverse()
        return 
    k = l-1
    while nums[k] >= nums[d]:
        d -= 1
    nums[k], nums[d] = nums[d], nums[k]
    while l < m:
        nums[l], nums[m] = nums[m], nums[l]
        l += 1; m -= 1

