```swift
class Solution {
    func getPermutation(_ n: Int, _ k: Int) -> String {
        var numbers = [Int](1...n)
        var k = k, factorial = 1, diff = n - 1, result = ""

        for i in 1..<n { factorial *= i }

        for _ in 0..<n {
            for (i, num) in numbers.enumerated() {
                if k > factorial {
                    k -= factorial
                } else {
                    result += String(num)
                    numbers.remove(at: i)
                    break
                }
            }
            if diff > 1 {
                factorial /= diff
                diff -= 1
            }
        }
        return result
    }
}
```

