    public String getPermutation(int n, int k) {

        // e.g n = 5
        // [1][1][2][6][24]
        int[] factorial = new int[n];
        factorial[0] = 1;

        for(int i = 1; i < n; i++) {
            factorial[i] = factorial[i-1] * i;
        }

        ArrayList<Integer> numbers = new ArrayList<>();
        for(int i = 1; i <= n; i++) {
            numbers.add(i);
        }

        String res = "";
        for(int i = n-1; i >= 0; i--) {

            int num = (k-1)/factorial[i];
            res += numbers.get(num);
            k -= num * factorial[i];
            numbers.remove(num);
        }

        return res;
    }
