class Solution {
public:
    string getPermutation(int n, int k) {
        int fact=1;
        vector<int> v;
        for(int i=1;i<=n;i++)
        {
            v.push_back(i);
            if(i!=n)
            fact*=i;
        }
        k-=1;
        string s;
        for(int i=0;i<n;i++)
        {
            int j=k/fact;
            k%=fact;
            if(n-i-1)
            fact/=(n-i-1);
            s+=('0'+v[j]);
            v.erase(v.begin()+j);
        }
        return s;
    }
};
```
