        int tk = k;
        for(int i=1;i<=n;i++){
            if(ismarked[i]) continue;
            if(tk-fact[numbers-1] <= 0){
                ismarked[i] = true;
                res+=to_string(i);
                Helper(n,numbers-1,tk,res,ismarked);
            }else{
                tk-=fact[numbers-1];
            }
        }
    }
    string getPermutation(int n, int k) {
        string res="";
        unordered_map<int,bool> ismarked;
        Fact(9);
        Helper(n,n,k,res,ismarked);
        return res;
    }
};
