                if(cur==')' && (tp=='{' || tp=='['))
                    return false;
                if(cur=='}' && (tp=='(' || tp=='['))
                    return false;
                if(cur==']' && (tp=='{' || tp=='('))
                    return false;
            }
        }
        return st.empty();
    }
};
