class Solution {
public:

    string getPermutation(int n, int k) {

        //Build the factorials memo.
        int aFac[n + 1];
        aFac[0] = 1;
        for (int i=1;i<=n;i++) { aFac[i] = i * aFac[i - 1]; }

        //Reverse the factorials list.
        int aLo = 0, aHi = n - 1;
        while (aLo < aHi) {
            swap(aFac[aLo++], aFac[aHi--]);
        }

        //This is our "bucket" of numbers to pick from.
        char aList[n + 1];
        for (int i=0;i<n;i++) {
            aList[i] = '1' + i;
        }
        int aListCount = n;

        //Storage for our result.
        char aRes[n + 1];
        aRes[n] = 0;


        int aIndex = 0;
        --k;

        for (int i=0;i<n;i++) {

            //The element we pick. One factorial less than N, divide k by this. This is our "first letter" for the "sub problem" ...
            aIndex = k / aFac[i];

            aRes[i] = aList[aIndex];

            //Delete from list.
            for (int j=aIndex + 1;j<aListCount;j++) { aList[j - 1] = aList[j]; }
            --aListCount;

            //Remove these permutations from remaining K.
            k -= aIndex * aFac[i];

        }

        return string(aRes);
    }
};
