class Solution:
    def getIntersectionNode(self, headA: ListNode, headB: ListNode) -> Optional[ListNode]:

        n,m=0,0
        tempA,tempB=headA,headB
        while tempA:
            n+=1
            tempA=tempA.next
        while tempB:
            m+=1
            tempB=tempB.next
        while m>n:
            headB=headB.next
            m-=1
        while n>m:
            headA=headA.next
            n-=1
        while headB!=headA:
            headB=headB.next
            headA=headA.next
        return headA
```
