class Solution {
public:
    ListNode *getIntersectionNode(ListNode *headA, ListNode *headB) {
        ListNode* pa = headA;
        while (pa) {
            for (ListNode* pb = headB; pb; pb = pb -> next) {
                if (pa == pb) {
                    return pa;
                }
            }
            pa = pa -> next;
        }
        return NULL;
    }
};
