class Solution {
public:
    ListNode *getIntersectionNode(ListNode *headA, ListNode *headB) {
        unordered_set<ListNode*> nodes;
        while (headA) {
            nodes.insert(headA);
            headA = headA -> next;
        }
        while (headB && nodes.find(headB) == nodes.end()) {
            headB = headB -> next;
        }
        return headB;
    }
};
