        // The key to make it O(1) space is **Each value on each linked list is in the range [1, 10^9].**
        // So just make each value negative to mark it as visited in the first list O(n) time
        // then iterate the second list until find a negative value, which is the answer O(m) time
        // and do another pass to put the positive value back to the first list O(m) time

        ListNode curr = headA;
        while (curr != null)
        {
            curr.val *= -1;
            curr = curr.next;
        }

        curr = headB;
        ListNode ans = null;
        while (curr != null)
        {
            if (curr.val < 1)
            {
                ans = curr;
                break;
            }  
            curr = curr.next;
        }

        curr = headA;
        while (curr != null)
        {
            curr.val = Math.Abs(curr.val);
            curr = curr.next;
        }

        return ans;
    }
}
```

