```
class Solution {
public:
    ListNode *getIntersectionNode(ListNode *headA, ListNode *headB) {

        ListNode* temp1=headA;
        ListNode* temp2=headB;
        int len1=0;
        int len2=0;
        while(headA!=NULL){
            headA=headA->next;
            len1++;

        }
         while(headB!=NULL){
            headB=headB->next;
            len2++;

        }
        int delta;
        if(len1>len2){
            delta=len1-len2;
        }
        else if(len1<len2){
            delta=len2-len1;
        }
        else{
            delta=0;
        }
        if(len1>len2){

            while(delta--){
                temp1=temp1->next;
            }
        }
        else if(len1<len2){
            while(delta--){
                temp2=temp2->next;
            }
        }
        //now we can move both pointers
        while(temp1!=temp2){
             temp1=temp1->next;
             temp2=temp2->next;
        }
        return temp1;
    }
};
