ListNode *getIntersectionNode(ListNode *headA, ListNode *headB){
        ios_base::sync_with_stdio(0);
        cin.tie(0);cout.tie(0);
        /* USING TWO POINTER*/
        ListNode* a = headA;
        ListNode* b = headB;
        while(a!=b){
            if(a==NULL){
                a=headB;
            }
            else{
                a = a -> next;
            }
            if(b==NULL){
                b=headA;
            }
            else{
                b = b -> next;
            }
        }
        return a;
    }

