 def getIntersectionNode(self, headA: ListNode, headB: ListNode) -> Optional[ListNode]:
        aLen = self.getLength(headA)
        bLen = self.getLength(headB)
        # print(f'|aLen| => {aLen}, |bLen| => {bLen}')

        a_ptr, b_ptr = headA, headB

        # move the pointers to the appropriate spots
        if aLen < bLen:
            # move b to start at the same end distance as a
            for i in range(bLen - aLen):
                b_ptr = b_ptr.next

        else:
            # move a to start at the same end distance as b
            for i in range(aLen - bLen):
                a_ptr = a_ptr.next

        # check to see if there is an intersection
        while a_ptr and b_ptr:
            if a_ptr == b_ptr:
                return a_ptr
            a_ptr = a_ptr.next
            b_ptr = b_ptr.next

        return None 

    def getLength(self, node: ListNode) -> int:
        _len = 0
        cur = node
        while cur:
            cur = cur.next
            _len += 1
        return _len
