        // XOr
        // Edge case 
        if(nums == null || nums.length == 0) return null;

        int xor = 0;
        for(int i : nums)
            xor ^= i;

        xor &= -xor;

        int[] res = {0,0};
        for(int i : nums) {
            if((i & xor) == 0)
                res[0] ^= i;
            else
                res[1] ^= i;
        }
        return res;
