class Solution {
public:
    vector<int> singleNumber(vector<int>& nums) {

        if(nums.size()==2)
        {
            //all are appeared once
            return nums;
        }

        //we calculate the xor of all elements as usual 
        int xorVal=0;
        for(auto i:nums)
            xorVal=xorVal^ i;
        //now we have the xor of two unique values
        //now we have to get the one of the unique value 
        //in xor the result is 1 if one value has set bit and other doesnot
        //we use this logic to get the one of the unique val 
        //get the first position of set bit in xorVal
        int pos=0;
        int temp=xorVal;
        while(!(temp&1)){
            pos++;
            temp>>=1;
        }
        //we will now calculate the xor values of all elements in the array which has set bit in position pos
        int tempXor=0;
        for(int i=0;i<nums.size();++i)
        {
            if(checkSetBit(nums[i],pos)){
                tempXor=tempXor ^ nums[i];
            }
        }
        //we have got one of the value in our answer  
        vector<int> res;
        res.push_back(tempXor);
        res.push_back(tempXor ^ xorVal); //gives the other unique value
        return res;
    }

    //checks if a set bit exists in position 'pos' in its binary representation
    bool checkSetBit(int num,int pos)
    {
        return (num&(1<<pos));
    }
};
