    //either num1 == 0 && num2 == 1 || num1 == 1 && num2 == 0

    int lastBitOne = 0;
    for(int i = 0; i < 32 ; i++){
        if((xorResult & 1) == 1){
            lastBitOne = i;
            break;
        }
        xorResult >>>= 1;
    }

    //now knowing the lastBitOne
